import React, {Component} from 'react';
import axios from 'axios';
import {withRouter} from 'react-router-dom';
import DocInfo from '../../components/doc-info/doc-info.component';
import SearchBox from '../../components/search-box/simple-search-box/simple-search-box.component';

const SEVER_URL = 'http://localhost:8090';
const QUERY_URL = 'query'

class QueryPage extends Component {
    // eslint-disable-next-line no-useless-constructor
    constructor(props) {
        super(props);
        this.state = {
            results: []
        }
    }

    componentDidMount() {
        console.log(this.props);
        const title = this.props.location.search.substring(1);
        console.log(title);
        axios
            .get(`${SEVER_URL}/${QUERY_URL}/title`, {
            params: {
                title: title
            }
        })
            .then(response => response.data)
            .then(response => this.setState({results: response}))
            .then(res => console.log(this.state.results))

    }

    render() {
        return (
            <div>
                <SearchBox/>

                <h1>This is the QueryPage</h1>

                {this
                    .state
                    .results
                    .map(docinfo => (<DocInfo key={docinfo.id} info={docinfo}/>))
}
            </div>
        );
    }
}

export default withRouter(QueryPage);
