import React, {Component} from 'react';
import axios from 'axios';
import {withRouter} from 'react-router-dom';
import qs from 'qs';
import DocInfo from '../../components/doc-info/doc-info.component';
import AdvancedSearchBox from '../../components/search-box/advanced-search-box/advanced-search-box.component';
import {Container} from 'reactstrap';

const SEVER_URL = 'http://localhost:8090';
const QUERY_URL = 'query'

class QueryPage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            curPage: 0,
            totalPage: -1,
            queryText: '',
            author: '',
            dateSince: '',
            dateTo: '',
            embedding: false,
            content: []
        }
    }

    // only called from the mainpage
    componentDidMount() {
        // the origin pass param from the 'location.search' field starts with '?', get
        // rid of it
        const text = this
            .props
            .location
            .search
            .substring(1);

        axios
            .get(`${SEVER_URL}/${QUERY_URL}/text`, {
            params: {
                text: text,
                page: this.state.curPage
            }
        })
            .then(response => response.data)
            .then(response => {
                this.setState({content: response.content, totalPage: response.totalPages});
            });
    }
    handleDateSinceChange = (date) => {
        this.setState({
            dateSince: date._d
        }, () => console.log(this.state.dateSince));
    }
    handleDateToChange = (date) => {
        this.setState({
            dateTo: date._d
        }, () => console.log(this.state.dateTo));
    }

    handleEmbeddingChange = (event) => {

        this.setState({
            embedding: !this.state.embedding
        }, () => console.log(this.state.embedding))
    }

    handelTextChange = (event) => {
        const {value, name} = event.target;
        this.setState({
            [name]: value
        }, () => console.log(name + ':' + value));
    }

    handleSubmit = (event) => {
        axios
            .get(`${SEVER_URL}/${QUERY_URL}`, {
            params: {
                text: this.state.queryText,
                author: this.state.author,
                dateSince: this.state.dateSince,
                dateTo: this.state.dateTo,
                embedding: this.state.embedding,
                page: this.state.curPage
            }
        })
            .then(response => response.data)
            .then(response => this.setState({content: response.content, totalPage: response.totalPages}));
    }

    render() {
        return (
            <div>
                <div
                    style={{
                    backgroundImage: "url(" + require("../../assets/img/covid19.jpg") + ")"
                }}
                    className="page-header page-header-xs"
                    data-parallax={true}>
                    <div className="filter"/>
                    <div className="motto text-center">
                        <h1 >
                            COVID-19 Advanced Search
                        </h1>
                    </div>
                </div>
                <div>
                    <AdvancedSearchBox
                        handelTextChange={this.handelTextChange}
                        handlesubmit={this.handleSubmit}
                        handleDateSinceChange={this.handleDateSinceChange}
                        handleDateToChange={this.handleDateToChange}
                        handleEmbeddingChange={this.handleEmbeddingChange}
                        states={this.state}/>
                </div>
                <Container style={{
                    marginTop: '-25px'
                }}>

                    {this
                        .state
                        .content
                        .map(docinfo => (<DocInfo key={docinfo.id} info={docinfo}/>))
}
                    <br/><br/>

                </Container>

            </div>
        );
    }
}

export default withRouter(QueryPage);
