import React, {Component} from 'react';
import axios from 'axios';
import {withRouter} from 'react-router-dom';
import qs from 'qs';
import DocInfo from '../../components/doc-info/doc-info.component';
import AdvancedSearchBox from '../../components/search-box/advanced-search-box/advanced-search-box.component';
import {Container} from 'reactstrap';

const SEVER_URL = 'http://localhost:8090';
const QUERY_URL = 'query'

class QueryPage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            curPage: 0,
            authors: [],
            results: []
        }
    }

    // only called from the mainpage
    componentDidMount() {
        // the origin pass param from the 'location.search' field starts with '?', get
        // rid of it
        const text = this
            .props
            .location
            .search
            .substring(1);

        axios
            .get(`${SEVER_URL}/${QUERY_URL}/text`, {
            params: {
                text: text,
                page: this.state.curPage
            }
        })
            .then(response => response.data)
            .then(response => this.setState({results: response.content}));
    }

    handelChange = (event) => {
        const {value, name} = event.target;
        this.setState({
            [name]: value
        }, () => console.log(name + ':' + value));
    }

    handleSubmit = (event) => {
        axios
            .get(`${SEVER_URL}/${QUERY_URL}`, {
            params: {
                title: this.state.title,
                abstract: this.state.abstract,
                author: this.state.author
            }
        })
            .then(response => response.data)
            .then(response => this.setState({results: response}));
    }

    render() {
        return (
            <div>
                <div
                    style={{
                    backgroundImage: "url(" + require("../../assets/img/covid19.jpg") + ")"
                }}
                    className="page-header page-header-xs"
                    data-parallax={true}>
                    <div className="filter"/>
                    <div className="motto text-center">
                        <h1 >
                            COVID-19 Advanced Search
                        </h1>
                    </div>
                </div>
                <div>
                    <AdvancedSearchBox
                        handlechange={this.handelChange}
                        handlesubmit={this.handleSubmit}
                        states={this.state}/>
                </div>
                <Container style={{
                    marginTop: '-25px'
                }}>

                    {this
                        .state
                        .results
                        .map(docinfo => (<DocInfo key={docinfo.id} info={docinfo}/>))
}

                </Container>

            </div>
        );
    }
}

export default withRouter(QueryPage);
