import React from 'react';
import {Container, Pagination, PaginationItem, PaginationLink} from 'reactstrap';

const PageNav = ({curPage, totalPage, handleNav}) => {
    curPage = curPage + 1;
    const pageNumbers = [];
    if (totalPage <= 9) {
        for (let i = 1; i <= totalPage; i++) {
            pageNumbers.push(i);
        }
    } else {
        // page1 disappear
        if (curPage <= 5) {
            console.log('here1');
            for (let i = 1; i <= 9; i++) {
                pageNumbers.push(i);
            }
        } else {
            for (let i = curPage - 4; i <= curPage + 4; i++) {
                pageNumbers.push(i);
            }
        }
    }
    console.log(pageNumbers);

    return (
        <nav aria-label="...">
            <Pagination>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={handleNav} tabIndex="-1">
                        Previous
                    </PaginationLink>
                </PaginationItem>
                {pageNumbers.map(pagenum => (pagenum !== curPage
                    ? <PaginationItem key={pagenum}>
                            <PaginationLink onClick={e => handleNav(pagenum)}>
                                {pagenum}
                            </PaginationLink>
                        </PaginationItem>
                    : <PaginationItem key={pagenum}>
                        <PaginationLink onClick={e => handleNav(pagenum)}>
                            {pagenum}
                            <span className="sr-only">(current)</span>
                        </PaginationLink>
                    </PaginationItem>))}
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={handleNav}>
                        Next
                    </PaginationLink>
                </PaginationItem>
            </Pagination>
        </nav>
    )
}
