import React from 'react';
import {Container, Pagination, PaginationItem, PaginationLink} from 'reactstrap';


const PageNav = ({curPage, totalPage, handleNav}) => {
    curPage = curPage + 1;
    const pageNumbers = [];
    if(totalPage <= 9) {
        for(let i = 1; i <= totalPage; i++) {
            pageNumbers.push(i);
        }
    } else {
        // page1 disappear
        console.log('here');
        if(curPage <= 5) {
            console.log('here1');
            for(let i = 1; i <= 9; i++) {
                console.log('here1');
                pageNumbers.push(i);
            }
        } else {
            for(let i = curPage-4; i <= curPage+4; i++) {
                pageNumbers.push(i);
            }
        }
    }
    console.log(pageNumbers);

    // const items = [];
    // pageNumbers.map(pagenum => {
    //     if(pagenum !== curPage) {
    //         items.push(<PaginationItem>
    //             <PaginationLink href="#pablo" onClick={e => e.preventDefault()} tabIndex="-1">
    //                 Previous
    //             </PaginationLink>
    //         </PaginationItem>);
    //     }
    // });

    return (
        <nav aria-label="...">
            <Pagination>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={handleNav} tabIndex="-1">
                        Previous
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={handleNav}>
                        1
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={handleNav}>
                        2
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem className="active">
                    <PaginationLink href="#pablo" onClick={handleNav}>
                        3
                        <span className="sr-only">(current)</span>
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={handleNav}>
                        4
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={handleNav}>
                        5
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={handleNav}>
                        Next
                    </PaginationLink>
                </PaginationItem>
            </Pagination>
        </nav>
    );
};

export default PageNav;
