import React, {Component} from 'react';
import axios from 'axios'
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
            title: "",
            results: []
        };
        this.handleChange = this
            .handleChange
            .bind(this);
        this.handleSubmit = this
            .handleSubmit
            .bind(this);
    }

    handleChange(event) {
        this.setState({title: event.target.value});
    }

    handleSubmit(event) {
        const query = () => {
            try {
                return axios.get("http://localhost:8090/query/title", {
                    params: {
                        title: this.state.title
                    }
                });
            } catch (error) {
                console.error(error);
            }
        }

        const startQuery = async() => {
            const q = query().then(response => {
                if (response.data.message) {
                    console.log(response.data.message);
                } else {
                  console.log("non");
                }
            }).catch(error => {
                console.log(error);
            })
        }

        startQuery();

        // axios
        //     .get("http://localhost:8090/query/title", {
        //     params: {
        //         title: this.state.title
        //     }
        // })
        //     .then(Response => Response.json())
        //     .then(Response => this.setState({results: Response}))
        //     .then(Response => console.log("hhh"))
        // // .then(Response => Response.json()) .then(Response => this.setState({results:
        // // Response})) .then(Response => console.log(this.state.title,
        // // this.state.results))

    }

    render() {
        return (
            <div className="App">
                <h1 >
                    IR Search Engine
                </h1>
                <p>
                    {this.state.results}</p>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        <input type="text" value={this.state.title} onChange={this.handleChange}/>
                    </label>
                    <input type="submit" value="Submit"/>
                </form>
            </div>
        );
    }
}

export default App;
