import React from 'react';
import {Container, Pagination, PaginationItem, PaginationLink} from 'reactstrap';


const PageNav = (curPage, totalPage) => {
    const pageNumbers = [];
    if(totalPage <= 9) {
        for(let i = 1; i <= totalPage; i++) {
            pageNumbers.push(i);
        }
    } else {
        // page1 disappear
        if(curPage <= 5) {
            for(let i = 1; i <= 9; i++) {
                pageNumbers.push(i);
            }
        } else {
            for(let i = curPage-4; i <= curPage+4; i++) {
                pageNumbers.push(i);
            }
        }
    }
    console.log(pageNumbers);
    return (
        <nav aria-label="...">
            <Pagination>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={e => e.preventDefault()} tabIndex="-1">
                        Previous
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={e => e.preventDefault()}>
                        1
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={e => e.preventDefault()}>
                        2
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem className="active">
                    <PaginationLink href="#pablo" onClick={e => e.preventDefault()}>
                        3
                        <span className="sr-only">(current)</span>
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={e => e.preventDefault()}>
                        4
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={e => e.preventDefault()}>
                        5
                    </PaginationLink>
                </PaginationItem>
                <PaginationItem>
                    <PaginationLink href="#pablo" onClick={e => e.preventDefault()}>
                        Next
                    </PaginationLink>
                </PaginationItem>
            </Pagination>
        </nav>
    );
};

export default PageNav;
