import React, {Component} from 'react';
import axios from 'axios';
import {withRouter} from 'react-router-dom';
import DocInfo from '../../components/doc-info/doc-info.component';
import AdvancedSearchBox from '../../components/search-box/advanced-search-box/advanced-search-box.component';

const SEVER_URL = 'http://localhost:8090';
const QUERY_URL = 'query'

class QueryPage extends Component {
    // eslint-disable-next-line no-useless-constructor
    constructor(props) {
        super(props);
        this.state = {
            title: '',
            abstract: '',
            author: '',
            results: []
        }
    }

    // only called from the mainpage
    componentDidMount() {
        console.log(this.props);
        const title = this.props.location.search.substring(1);
        console.log(title);
        axios
            .get(`${SEVER_URL}/${QUERY_URL}/title`, {
            params: {
                title: title
            }
        })
            .then(response => response.data)
            .then(response => this.setState({results: response}))
            .then(res => console.log(this.state.results))
    }

    handelChange = (event) => {
        const { value, name } = event.target;
        this.setState({[name]: value}, () => console.log(name + ':' + value));
    }

    handleSubmit = (event) => {
        axios
            .get(`${SEVER_URL}/${QUERY_URL}`, {
            params: {
                title: this.state.title
            }
        })
            .then(response => response.data)
            .then(response => this.setState({results: response}))
            .then(res => console.log(this.state.results))
    }

    render() {
        return (
            <div>
                <AdvancedSearchBox handlechange={this.handelChange} handlesubmit={this.handleSubmit} states={this.state}/>

                <h1>This is the QueryPage</h1>

                {this
                    .state
                    .results
                    .map(docinfo => (<DocInfo key={docinfo.id} info={docinfo}/>))
}
            </div>
        );
    }
}

export default withRouter(QueryPage);
