import React from 'react';

const DocInfo = (props) => (
    <div>
        <Row></Row>
        <h3 className="mb-1">Titile: {props.info.title}</h3>
        <p>Authors: {props.info.authors}</p>
        <p>Abstract: {props.info.textAbstract.substring(0, 800) + '...'}</p>
    </div>
);

export default DocInfo;
