import React, {Component} from 'react';
import { withRouter} from 'react-router-dom';
import { Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import {Button, Container} from "reactstrap";
// import './search-box.styles.css';

class SimpleSearchBox extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: ''
        };
    }

    handleChange = (event) => {
        this.setState({title: event.target.value});
    }

    handleSubmit = (event) => {
        event.preventDefault();
        this
            .props
            .history
            .push({pathname: '/query', search: '?title:' + this.state.title});
    }

    render() {
        return (
            <div className="App">
                <Form onSubmit={this.handleSubmit}>
                    <Input type="text" value={this.state.title} onChange={this.handleChange}/>
                    <br/>
                    <Button className="btn-round" color="neutral" type="submit" value="Submit" outline>Submit</Button>
                    <Button className="btn-round" color="neutral" value="Submit" styles={{margging: '100px'}} outline > Advanced Search</Button>
                </Form>
            </div>
        );
    }
}

export default withRouter(SimpleSearchBox);
